javaImport = new JavaImporter()
javaImport.importPackage(
	Packages.okhttp3,
	Packages.cn.hutool.core.util,
	Packages.cn.hutool.core.codec,
	Packages.cn.hutool.crypto.digest
)
with (javaImport) {
	//æ‰‹æœºå‹å·
	prop = SystemPropsUtil.getProps()
	device = String(prop).split(" ")[6]

	//POSTè¯·æ±‚
	function post(url, body, map) {
		json = MediaType.parse("application/json")
		request = new Request.Builder()
			.url(url)
			.post(RequestBody.create(body, json))
		if (map) {
			request.addHeader("Content-Encoding", "gzip")
			for (n in map) {
				request.addHeader(n, map[n])
			}
		}
		client = new OkHttpClient()
		response = client.newCall(request.build()).execute()
		res = JSON.parse(response.body().string())
		return res
	}

	//gzipå‹ç¼©
	gzip = (data) => ZipUtil.gzip(data, "")

	//gzipè§£å‹
	ungz = (data) => ZipUtil.unGzip(data, "")

	//Base64ç¼–ç 
	b64En = (b64) => Base64.encode(b64)

	//Base64è§£ç 
	b64De = (b64) => Base64.decode(b64)

	//ttEncrypt
	function TT(data) {
		const {java} = this
		data = gzip(data)

		//SHA512åŠ å¯†
		sha = (arr) => DigestUtil.sha512(arr)

		arr = b64De("TdTC5rgxYgkOUrPHpnM7pByyRiuCmrWKGWs521cXdST0m69/COjWjSanLjfBqVovHwWlGJKu8pSXMrYqOKrdWA==")
		value = RandomUtil.randomBytes(32)
		arr = sha(sha(value).concat(arr))
		key = arr.slice(0, 16)
		iv = arr.slice(16, 32)
		data = sha(data).concat(data)
		data = java.createSymmetricCrypto("AES/CBC/PKCS5Padding", key, iv).encrypt(data)

		head = [116, 99, 5, 16, 0, 0]
		data = head.concat(value, data)
		return data
	}

	//X-Gorgonå‚æ•°
	function xGorgon(url, data) {
		const {java} = this
		params = String(url).split("?")[1]

		//md5åŠ å¯†
		md5 = (str) => DigestUtil.md5Hex(str)
		function getHex() {
			let hex = md5(params)
			hex += data ? md5(data) : "0".repeat(32)
			hex += "0".repeat(32)
			return hex
		}

		//åè½¬å­—ç¬¦ä¸²
		rStr = (str) => StrUtil.reverse(str)

		//10è½¬16è¿›åˆ¶
		Hex = (num) => num.toString(16).padStart(2, "0")

		//åè½¬16è¿›åˆ¶
		rHex = (num) => parseInt(rStr(Hex(num)), 16)

		//åè½¬2è¿›åˆ¶
		function rBin(num) {
			bin = num.toString(2).padStart(8, "0")
			return parseInt(rStr(bin), 2)
		}

		//è¿ç®—
		function calculate(hex) {
			len = 0x14
			key = [0xDF, 0x77, 0xB9, 0x40, 0xB9, 0x9B, 0x84, 0x83, 0xD1, 0xB9, 0xCB, 0xD1, 0xF7, 0xC2, 0xB9, 0x85, 0xC3, 0xD0, 0xFB, 0xC3]
			paramList = []
			for (i = 0; i < 9; i += 4) {
				temp = hex.substring(8 * i, 8 * (i + 1))
				for (j = 0; j < 4; j ++) {
					h = parseInt(temp.substring(j * 2, (j + 1) * 2), 16)
					paramList.push(h)
				}
			}
			paramList.push(0x0, 0x6, 0xB, 0x1C)
			T = Math.floor(Date.now() / 1000)
			paramList.push((T >> 24) & 0xFF, (T >> 16) & 0xFF, (T >> 8) & 0xFF, T & 0xFF)
			//å¼‚æˆ–
			eorResultList = []
			for (i = 0; i < paramList.length; i ++) {
				eorResultList.push(paramList[i] ^ key[i % len])
			}
			for (i = 0; i < len; i ++) {
				A = rHex(eorResultList[i])
				B = eorResultList[(i + 1) % len]
				C = A ^ B
				D = rBin(C)
				F = ((D ^ 0xFFFFFFFF) ^ len) & 0xFF
				eorResultList[i] = F
			}
			result = ""
			for (param of eorResultList) {
				result += Hex(param)
			}
			option = {
				"headers": {
					"X-Khronos": String(T),
					"X-Gorgon": "0404b0d30000" + result
				}
			}
			if (data) {
				option = {
					"body": b64En(data),
					"headers": option.headers
				}
			}
			return JSON.stringify(option)
		}
		return url + "," + calculate(getHex())
	}
}

