var u=source.getKey(),
v=source.getVariable(),
key=java.get("key"),
set=(url,body)=>{
	java.log("set ä¿å­˜ä¸­â€¦")
	obj={url:url,body:body}
	str=JSON.stringify(obj)
	source.setVariable(str)
	};
if(!v||v==""){
	var html=java.ajax(u),so=[],
	j=org.jsoup.Jsoup.parse(html);
	a=j.select("form").get(0).attr("action");
	f=j.select("form").get(1).select("input");
	java.log("init åˆå§‹åŒ–æ‰§è¡Œä¸­â€¦")
	for(i in f){
		name=f[i].attr("name")
		value=f[i].attr("value")
		type=f[i].attr("type")
		if(type=="text"){
			add=`${name}={{key}}`;
			}else{
				add=`${name}=${value}`
				}
			so.push(add)
		}
		sk=so.join("&")
		//java.log(sk)
		set(a,sk)
		s=a;
		body=sk;
	}else{
		$=JSON.parse(v)
		body=$.body
		s=$.url;
		}
body=body.replace("{{key}}",key);
s;
body;

