let register_url = "https://log.snssdk.com/service/2/device_register/"

function random_device_udid() {
  let result = ''
  const char = '0123456789abcdef'
  // 随机40位udid
  for (let i = 0; i < 40; i++) {
    result += char.charAt(Math.floor(Math.random() * char.length))
  }
  return result
}

function random_device_model() {
  let result = ''
  const char = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ'
  // 生成第一段型号
  for (let i = 0; i < 3; i++) {
    result += char.charAt(Math.floor(Math.random() * char.length))
  }
  result += '-'
  // 第二段
  for (let i = 0; i < 5; i++) {
    result += char.charAt(Math.floor(Math.random() * char.length))
  }
  return result
}

function device_register(is_random) {
  // 检测是否是随机设备进行注册
  if (typeof is_random == "undefined") {
    is_random = false
  }

  // 声明函数内变量
  let udid
  let model
  if (is_random) {
    // 随机设备信息
    udid = random_device_udid()
    model = random_device_model()
  } else {
    // 本机信息
    let device_uuid = java.md5Encode(java.androidId())
    udid = device_uuid + java.md5Encode(device_uuid).slice(0, 8)
    model = device
  }

  if (source.getLoginHeader() && !is_random && source.getLoginHeader().includes(device)) {
    java.toast("本设备已注册，无需重复注册")
    return
  }

  java.toast("开始进行设备注册: \n设备型号: " + model + "\n设备id: " + udid)
  java.log("番茄免费小说: 开始进行设备注册: \n设备型号: " + model + "\n设备id: " + udid)

  var data = JSON.stringify({
    "header": {
      "package": "com.dragon.read",
      "openudid": udid, // openudid是40位hex
      "device_model": model
    }
  })

  const res = post(register_url, TT(data))

  const header = "&aid=1967&app_name=novelapp&channel=0&device_id=" + res.device_id + "&device_platform=android&device_type=" + model + "&iid=" + res.install_id + "&os_version=0&version_code=99999"

  source.putLoginHeader(header)
  java.toast("设备注册成功！")
}

function device_register_random() {
  device_register(true)
}

function logout() {
  cookie.removeCookie("fanqienovel.com")
  cookie.removeCookie("snssdk.com")
  java.toast("退出登录成功")
}

function browser_login() {
  // java.removeCookie("snssdk.com")
	var cookie = java.getCookie("fanqienovel.com")
	java.log(cookie)
	if (cookie && cookie != "") {
	  java.toast("请不要重复登录，请先退出登录！")
	  return false
	}
	try {
    java.startBrowserAwait("https://fanqienovel.com/", "登录")
  } catch (e) {
    java.toast(e)
  }
  var cookie = java.getCookie("fanqienovel.com")
  java.log("snssdk cookie: " + java.getCookie("snssdk.com") + "will be reomved")
  try {
    cookie.removeCookie("snssdk.com")
  } catch (e) {}
  java.log(cookie)
  if (!cookie || cookie == "") {
    java.toast("未获取到登录凭据，登陆失败")
    return false
  }
  // cookie.removeCookie("snssdk.com")
  java.toast("登录成功！")
  return true
}

